 var id = "cbe752bcdb1f10104c815c7a48961908";
 var restApi = new unity_brocade_restapi(id);
 var jobVal = workflow.scratchpad.jobIdVal;
 var url = 'api/v2/jobs/'+ jobVal + '/stdout/';
 var jsonTry =  new global.JSON();
 var rest = restApi.RESTCall(url, "get", "");
 var pattern = /FAILED[\W\s]+changed\\\"\s*:\s*false,\s*(\\\"msg\\\":\s*.*\\\")/
 var output = JSON.parse(rest.responseBody);
 var qry = JSON.stringify((output));
 if(qry.match(pattern)) {
	var msg = qry.match(pattern);
    workflow.scratchpad.msg = msg;
	gs.info(msg);
	}
 else{
    gs.info('Check for the failed msg in AWX');
 }

